import React, { useState } from 'react';
import { Upload, FileText, Download, AlertCircle, CheckCircle, Loader } from 'lucide-react';

const LANGUAGES = [
    { code: 'ko', name: 'ÌïúÍµ≠Ïñ¥' },
    { code: 'en', name: 'English' },
    { code: 'ja', name: 'Êó•Êú¨Ë™û' },
    { code: 'zh', name: '‰∏≠Êñá' },
    { code: 'es', name: 'Espa√±ol' },
    { code: 'fr', name: 'Fran√ßais' },
];

export default function App() {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [results, setResults] = useState([]);
    const [error, setError] = useState(null);
    const [sourceLang, setSourceLang] = useState('ko');
    const [targetLang, setTargetLang] = useState('en');
    const [columnName, setColumnName] = useState('text');

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        if (selectedFile) {
            setFile(selectedFile);
            setError(null);
            setResults(null);
        }
    };

    const handleUpload = async () => {
        if (!file) {
            setError('ÌååÏùºÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî');
            return;
        }

        setLoading(true);
        setError(null);

        const formData = new FormData();
        formData.append('file', file);

        console.log('üì§ ÏóÖÎ°úÎìú ÏãúÏûë:', {
            fileName: file.name,
            fileSize: file.size,
            sourceLang,
            targetLang,
            columnName
        });

        try {
            const apiUrl = `http://localhost:8000/api/v1/upload?source_lang=${sourceLang}&target_lang=${targetLang}&text_column=${columnName}`;
            console.log('üåê API URL:', apiUrl);

            const response = await fetch(apiUrl, {
                method: 'POST',
                body: formData,
            });

            console.log('üì• ÏùëÎãµ ÏÉÅÌÉú:', response.status);

            if (!response.ok) {
                const errorData = await response.json();
                console.error('‚ùå ÏóêÎü¨ ÏùëÎãµ:', errorData);
                throw new Error(errorData.detail || 'ÏóÖÎ°úÎìú Ïã§Ìå®');
            }

            const data = await response.json();
            console.log('‚úÖ Î≤àÏó≠ ÏôÑÎ£å:', data);
            setResults(data);
        } catch (err) {
