// hooks/useTranslation.js 수정 버전

import { useState } from 'react';

export const useTranslation = () => {
    const [translating, setTranslating] = useState(false);
    const [results, setResults] = useState(null);
    const [error, setError] = useState(null);

    const translate = async (file, columnName, sourceLang, targetLang) => {
        setTranslating(true);
        setError(null);

        const formData = new FormData();
        formData.append('file', file);

        try {
            // ⭐ 핵심: 업로드와 번역을 한 번에!
            const response = await fetch(
                `http://localhost:8000/api/v1/upload?source_lang=${sourceLang}&target_lang=${targetLang}&text_column=${columnName}`,
                {
                    method: 'POST',
                    body: formData,
                }
            );

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.detail || '번역 실패');
            }

            const data = await response.json();
            setResults(data);
            return data;
        } catch (err) {
            setError(err.message);
            throw err;
        } finally {
            setTranslating(false);
        }
    };

    const reset = () => {
        setTranslating(false);
        setResults(null);
        setError(null);
    };

    return {
        translating,
        results,
        error,
        translate,
        reset,
    };
};

// ====================================
// TranslationPage.jsx 수정 버전
// ====================================

export const TranslationPage = () => {
    const [file, setFile] = useState(null);

    const {
        translating,
        results,
        error,
        translate,
        reset
    } = useTranslation();

    const handleFileSelection = (selectedFile) => {
        setFile(selectedFile);
        reset();
    };

    const handleUploadAndTranslate = async ({ sourceLang, targetLang, columnName }) => {
        if (!file) {
            alert('파일을 선택해주세요');
            return;
        }

        // ⭐ 간단하게: 파일, 컬럼명, 언어만 전달
        await translate(file, columnName, sourceLang, targetLang);
    };

    return (
        <PageContainer>
            <PageHeader>
                <PageTitle>엑셀 번역 서비스</PageTitle>
                <PageDescription>
                    FastAPI + Pandas + React 18
                </PageDescription>
            </PageHeader>

            <ContentContainer>
                <FileUpload
                    onFileSelect={handleFileSelection}
                    onUpload={handleUploadAndTranslate}
                    file={file}
                    uploading={translating}
                    error={error}
                />

                {results && <ResultTable results={results} />}
            </ContentContainer>
        </PageContainer>
    );
};
